new_questions = json.loads(new_questions_data)['results']

all_questions = existing_questions

for new_question in new_questions:
    # Check for duplicates
    is_duplicate = False
    for existing_question in existing_questions:
        if new_question['question'] == existing_question['question']:
            is_duplicate = True
            break
    
    if not is_duplicate:
        # Convert to application format
        responses = new_question['incorrect_answers']
        responses.append(new_question['correct_answer'])
        correct_response = len(responses) - 1
        
        all_questions.append({
            'question': new_question['question'],
            'responses': responses,
            'correctResponse': correct_response
        })

with open('questions.json', 'w') as f:
    json.dump(all_questions, f, indent=2)
